const{ApolloServer:ApolloServer,gql:gql}=require("apollo-server"),typeDefs=gql`
    type SkiDay {
        id: ID!
        date: String!
        mountain: String!
    }

    type Query {
        totalDays: Int!
        allDays: [SkiDay!]!
    }
`,server=new ApolloServer({typeDefs:typeDefs,mocks:!0});server.listen().then(({url:url})=>console.log(`Server running at ${url}`));